query GetShowcaseSweeps($modelId: ID!) {
  model(id: $modelId) {
    id
    ...OverlayLayerExists
    locations(tags: "showcase") {
      ...AnchorLocationDetails
      __typename
    }
    __typename
  }
}

fragment OverlayLayerExists on Model {
  layers(types: ["matterport.overlay.assets"]) {
    id
    __typename
  }
  __typename
}

fragment AnchorLocationDetails on AnchorLocation {
  id
  index
  floor {
    id
    meshId
    __typename
  }
  room {
    id
    meshId
    __typename
  }
  neighbors
  tags
  position {
    x
    y
    z
    __typename
  }
  pano {
    id
    sweepUuid
    label
    placement
    source
    position {
      x
      y
      z
      __typename
    }
    rotation {
      x
      y
      z
      w
      __typename
    }
    ...SkyboxUrlDetails
    __typename
  }
  __typename
}

fragment SkyboxUrlDetails on PanoramicImageLocation {
  id
  resolutions
  skyboxes {
    ...SkyboxDetails
    __typename
  }
  __typename
}

fragment SkyboxDetails on SkyboxPanorama {
  resolution
  status
  urlTemplate
  tileResolution
  tileCount
  tileUrlTemplate
  validUntil
  __typename
}
