query GetViews($modelId: ID!) {
  model(id: $modelId) {
    id
    views {
      ...ViewDetails
      __typename
    }
    __typename
  }
}

fragment ViewDetails on ModelView {
  id
  type
  name
  enabled
  created
  modified
  layers {
    layer {
      id
      created
      modified
      type
      name
      label
      __typename
    }
    position
    visible
    __typename
  }
  __typename
}
